    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        try{

            String a = t1.getText();
            String b = t2.getText();
            String c = t3.getText();

            Class.forName("com.mysql.jdbc.Driver").newInstance();
            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost:3306/ticketbookingsystem","root", "");
            Statement st = cn.createStatement();
            st.executeUpdate("insert into train values ('"+ a +"', '" + b + "', '" + c + "')");
        }

        catch(Exception e){
            System.out.println(e);
        }

        TrainOperations t01 =new TrainOperations();
        t01.setVisible(true);
        
        dispose();
    }//GEN-LAST:event_jButton1ActionPerformed

